CREATE DATABASE IF NOT EXISTS ev_swap;
USE ev_swap;

CREATE TABLE IF NOT EXISTS users (
  id INT AUTO_INCREMENT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  email VARCHAR(100) UNIQUE,
  password VARCHAR(255)
);
select*from users;

CREATE TABLE IF NOT EXISTS stations (
  id INT AUTO_INCREMENT PRIMARY KEY,
  name VARCHAR(100),
  address VARCHAR(255),
  city VARCHAR(100),
  pincode VARCHAR(10),
  battery_types VARCHAR(255),
  open_time VARCHAR(20),
  close_time VARCHAR(20)
);
DROP TABLE stations;
DROP TABLE Newstations;


CREATE TABLE stations (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(100),
    address TEXT,
    city VARCHAR(100),
    pincode VARCHAR(10),
    battery_types VARCHAR(255),
    open_hours VARCHAR(100),
    status VARCHAR(20)
);


